t_name, :last_name, :username, :address, :about)\n    end\nend\n","undoManager":{"mark":35,"position":36,"stack":[[{"start":{"row":11,"column":10},"end":{"row":12,"column":0},"action":"insert","lines":["",""],"id":2},{"start":{"row":12,"column":0},"end":{"row":12,"column":4},"action":"insert","lines":["    "]}],[{"start":{"row":12,"column":4},"end":{"row":12,"column":5},"action":"insert","lines":["@"],"id":3}],[{"start":{"row":12,"column":5},"end":{"row":12,"column":6},"action":"insert","lines":["l"],"id":4}],[{"start":{"row":12,"column":6},"end":{"row":12,"column":7},"action":"insert","lines":["u"],"id":5}],[{"start":{"row":12,"column":7},"end":{"row":12,"column":8},"action":"insert","lines":["s"],"id":6}],[{"start":{"row":12,"column":8},"end":{"row":12,"column":9},"action":"insert","lines":["e"],"id":7}],[{"start":{"row":12,"column":9},"end":{"row":12,"column":10},"action":"insert","lines":["r"],"id":8}],[{"start":{"row":12,"column":10},"end":{"row":12,"column":11},"action":"insert","lines":[" "],"id":9}],[{"start":{"row":12,"column":11},"end":{"row":12,"column":12},"action":"insert","lines":["="],"id":10}],[{"start":{"row":12,"column":12},"end":{"row":12,"column":13},"action":"insert","lines":[" "],"id":11}],[{"start":{"row":12,"column":13},"end":{"row":12,"column":14},"action":"insert","lines":["U"],"id":12}],[{"start":{"row":12,"column":14},"end":{"row":12,"column":15},"action":"insert","lines":["s"],"id":13}],[{"start":{"row":12,"column":15},"end":{"row":12,"column":16},"action":"insert","lines":["e"],"id":14}],[{"start":{"row":12,"column":16},"end":{"row":12,"column":17},"action":"insert","lines":["r"],"id":15}],[{"start":{"row":12,"column":17},"end":{"row":12,"column":18},"action":"insert","lines":["."],"id":16}],[{"start":{"row":12,"column":18},"end":{"row":12,"column":19},"action":"insert","lines":["f"],"id":17}],[{"start":{"row":12,"column":19},"end":{"row":12,"column":20},"action":"insert","lines":["i"],"id":18}],[{"start":{"row":12,"column":20},"end":{"